sys_script_include.script:
var NeedItUtils = Class.create();
NeedItUtils.prototype = {
    initialize: function() {
    },
	isDatePast: function(strDate){
		// Create GlideDateTime objects for the current date and the passed in date
		var rightnow = new GlideDateTime();
		var testdate = new GlideDateTime(strDate);
		// they both have timestamps - we need just the dates
		var datetoday = rightnow.getLocalDate();
		var datetest = testdate.getLocalDate();

		// If the testdate before rightnow, return true, else return false
		if (datetoday.compareTo(datetest) > 0) {
			return true;
		}
		else {
			return false;
		}	
	},
	isDateToday: function(strDate){
		// Create GlideDate objects for the current date and the passed in date
		var rightnow = new GlideDateTime();
		var testdate = new GlideDateTime(strDate);
		// they both have timestamps - we need just the dates
		var datetoday = rightnow.getLocalDate();
		var datetest = testdate.getLocalDate();

		// If the testdate equals rightnow, return true, else return false
		if (datetoday.compareTo(datetest) == 0) {
			return true;
		}
		else {
			return false;
		}
	},
    type: 'NeedItUtils'
};
